import com.eviware.soapui.support.XmlHolder;
def requestXML = new XmlHolder( messageExchange.requestContentAsXml );
def responseXML = new XmlHolder(messageExchange.responseContentAsXml );
responseXML.namespaces["ns1"] = "http://tempuri.org/";
def expected = (requestXML["//tem:Add/tem:intA"] as Integer) + (requestXML["//tem:Add/tem:intB"] as Integer);
def actual = responseXML["//ns1:AddResult"] as Integer;
assert expected == actual;

import com.eviware.soapui.support.XmlHolder;
def requestXML = new XmlHolder( messageExchange.requestContentAsXml );
def responseXML = new XmlHolder(messageExchange.responseContentAsXml );
responseXML.namespaces["ns1"] = "http://tempuri.org/";
def expected = (requestXML["//tem:Divide/tem:intA"] as Integer) / (requestXML["//tem:Divide/tem:intB"] as Integer);
def actual = responseXML["//ns1:DivideResult"] as Integer;

assert expected == actual;
